mapper.select:
SELECT A.JOB_SEQ_ID AS jobSequenceId FROM JOB_CONTROL A
A.JOB_APP_CD = #{jobAppCd}
A.CUR_APP_STATUS = #{curAppStatus}
ORDER BY A.CUR_APP_STATUS DESC, A.JOB_SEQ_ID

mapper.update:
UPDATE JOB_CONTROL
UPD_DATE_TIME = CURRENT_TIMESTAMP,
BLOGIC_APP_STATUS = #{blogicAppStatus},
CUR_APP_STATUS = #{curAppStatus}
WHERE JOB_SEQ_ID = #{jobSequenceId}
